            swap(&a[0],&a[j])
            heapadjust(a,0,j)
        }

    }
    func heapadjust(a []int,i,length int){

        var left int = 2*i +1
        if left < length{
            maxIndex := left
            right := left + 1
            if a[right] > a[left] && right < length{
                maxIndex = right
            }
            if a[maxIndex] > a[i]{
                swap(&a[maxIndex],&a[i])
                heapadjust(a,maxIndex,length)
            }
        }
    }
